select patients.subject_id, patients.anchor_age from patients join (select distinct p1.subject_id from procedures_icd as p1, procedures_icd as p2 where p1.subject_id = p2.subject_id and p1.hadm_id <> p2.hadm_id and p1.icd_version = p2.icd_version and p1.icd_code = p2.icd_code order by p1.subject_id) as f on patients.subject_id = f.subject_id where patients.anchor_age<50  order by patients.subject_id, patients.anchor_age